pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/ERC20.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

/**
 * @title SimpleDEXV2
 * @notice Advanced DEX with ERC20 LP tokens, fee distribution, TWAP, and slippage protection
 * @dev Upgraded version with all advanced features
 */
contract SimpleDEXV2 is ERC20, ReentrancyGuard {
    // Token addresses
    IERC20 public immutable tokenA;
    IERC20 public immutable tokenB;

    // Pool reserves
    uint256 public reserveA;
    uint256 public reserveB;

    // Fee tracking (0.3% = 3/1000)
    uint256 public constant FEE_NUMERATOR = 3;
    uint256 public constant FEE_DENOMINATOR = 1000;

    // Accumulated fees for LP providers
    uint256 public accumulatedFeesA;
    uint256 public accumulatedFeesB;

    // TWAP: Time-Weighted Average Price Oracle
    struct Observation {
        uint256 timestamp;
        uint256 priceACumulative;
        uint256 priceBCumulative;
    }
    Observation[] public observations;
    uint256 private constant OBSERVATION_WINDOW = 24 hours;

    // Last price update timestamp
    uint256 public lastPriceUpdate;

    // Events
    event LiquidityAdded(
        address indexed provider,
        uint256 amountA,
        uint256 amountB,
        uint256 liquidityMinted
    );
    event LiquidityRemoved(
        address indexed provider,
        uint256 amountA,
        uint256 amountB,
        uint256 liquidityBurned
    );
    event Swap(
        address indexed user,
        address indexed tokenIn,
        uint256 amountIn,
        uint256 amountOut,
        uint256 priceImpact
    );
    event FeesClaimed(
        address indexed provider,
        uint256 feeA,
        uint256 feeB
    );

    /**
     * @notice Constructor sets the token pair and creates LP token
     * @param _tokenA Address of first token
     * @param _tokenB Address of second token
     */
    constructor(address _tokenA, address _tokenB)
        ERC20("SimpleDEX LP", "SLP-LP")
    {
        require(_tokenA != address(0) && _tokenB != address(0), "Invalid token address");
        require(_tokenA != _tokenB, "Tokens must be different");

        tokenA = IERC20(_tokenA);
        tokenB = IERC20(_tokenB);
        lastPriceUpdate = block.timestamp;
    }

    /**
     * @notice Add liquidity to the pool
     * @param amountA Amount of token A to add
     * @param amountB Amount of token B to add
     * @return liquidityMinted Amount of LP tokens minted
     */
    function addLiquidity(uint256 amountA, uint256 amountB)
        external
        nonReentrant
        returns (uint256 liquidityMinted)
    {
        require(amountA > 0 && amountB > 0, "Amounts must be greater than 0");

        // Update TWAP before changing reserves
        _updatePrice();

        // Transfer tokens from user to contract
        tokenA.transferFrom(msg.sender, address(this), amountA);
        tokenB.transferFrom(msg.sender, address(this), amountB);

        // Calculate liquidity to mint
        uint256 _totalSupply = totalSupply();
        if (_totalSupply == 0) {
            // First liquidity provider: liquidity = sqrt(amountA * amountB)
            liquidityMinted = _sqrt(amountA * amountB);
        } else {
            // Subsequent liquidity: maintain pool ratio
            uint256 liquidityA = (amountA * _totalSupply) / reserveA;
            uint256 liquidityB = (amountB * _totalSupply) / reserveB;
            liquidityMinted = liquidityA < liquidityB ? liquidityA : liquidityB;
        }

        require(liquidityMinted > 0, "Insufficient liquidity minted");

        // Mint LP tokens to user
        _mint(msg.sender, liquidityMinted);

        // Update reserves
        reserveA += amountA;
        reserveB += amountB;

        emit LiquidityAdded(msg.sender, amountA, amountB, liquidityMinted);
    }

    /**
     * @notice Remove liquidity from the pool
     * @param liquidityAmount Amount of LP tokens to burn
     * @return amountA Amount of token A returned
     * @return amountB Amount of token B returned
     */
    function removeLiquidity(uint256 liquidityAmount)
        external
        nonReentrant
        returns (uint256 amountA, uint256 amountB)
    {
        require(liquidityAmount > 0, "Amount must be greater than 0");
        require(balanceOf(msg.sender) >= liquidityAmount, "Insufficient liquidity");

        // Update TWAP before changing reserves
        _updatePrice();

        uint256 _totalSupply = totalSupply();

        // Calculate amounts to return (proportional to share)
        amountA = (liquidityAmount * reserveA) / _totalSupply;
        amountB = (liquidityAmount * reserveB) / _totalSupply;

        require(amountA > 0 && amountB > 0, "Insufficient liquidity burned");

        // Burn LP tokens
        _burn(msg.sender, liquidityAmount);

        // Update reserves
        reserveA -= amountA;
        reserveB -= amountB;

        // Transfer tokens back to user
        tokenA.transfer(msg.sender, amountA);
        tokenB.transfer(msg.sender, amountB);

        emit LiquidityRemoved(msg.sender, amountA, amountB, liquidityAmount);
    }

    /**
     * @notice Swap one token for another with slippage protection
     * @param tokenIn Address of token to swap in
     * @param amountIn Amount of token to swap in
     * @param minAmountOut Minimum output amount (slippage protection)
     * @return amountOut Amount of token received
     */
    function swap(
        address tokenIn,
        uint256 amountIn,
        uint256 minAmountOut
    ) external nonReentrant returns (uint256 amountOut) {
        require(amountIn > 0, "Amount must be greater than 0");
        require(
            tokenIn == address(tokenA) || tokenIn == address(tokenB),
            "Invalid token"
        );

        // Update TWAP before swap
        _updatePrice();

        // Determine input/output tokens and reserves
        bool isTokenA = tokenIn == address(tokenA);
        (IERC20 tokenInContract, IERC20 tokenOutContract) = isTokenA
            ? (tokenA, tokenB)
            : (tokenB, tokenA);
        (uint256 reserveIn, uint256 reserveOut) = isTokenA
            ? (reserveA, reserveB)
            : (reserveB, reserveA);

        // Calculate price impact BEFORE swap
        uint256 priceImpact = _calculatePriceImpact(amountIn, reserveIn, reserveOut);

        // Transfer input token from user
        tokenInContract.transferFrom(msg.sender, address(this), amountIn);

        // Calculate output amount with fee
        uint256 amountInWithFee = amountIn * (FEE_DENOMINATOR - FEE_NUMERATOR);
        uint256 numerator = amountInWithFee * reserveOut;
        uint256 denominator = (reserveIn * FEE_DENOMINATOR) + amountInWithFee;
        amountOut = numerator / denominator;

        require(amountOut > 0, "Insufficient output amount");
        require(amountOut >= minAmountOut, "Slippage exceeded");
        require(amountOut < reserveOut, "Insufficient liquidity");

        // Calculate fee
        uint256 feeAmount = (amountIn * FEE_NUMERATOR) / FEE_DENOMINATOR;

        // Update accumulated fees
        if (isTokenA) {
            accumulatedFeesA += feeAmount;
            reserveA += amountIn;
            reserveB -= amountOut;
        } else {
            accumulatedFeesB += feeAmount;
            reserveB += amountIn;
            reserveA -= amountOut;
        }

        // Transfer output token to user
        tokenOutContract.transfer(msg.sender, amountOut);

        emit Swap(msg.sender, tokenIn, amountIn, amountOut, priceImpact);
    }

    /**
     * @notice Claim accumulated fees proportional to LP share
     * @return feeA Amount of token A fees claimed
     * @return feeB Amount of token B fees claimed
     */
    function claimFees() external nonReentrant returns (uint256 feeA, uint256 feeB) {
        uint256 userBalance = balanceOf(msg.sender);
        require(userBalance > 0, "No liquidity");

        uint256 _totalSupply = totalSupply();

        // Calculate user's share of fees
        uint256 userShare = (userBalance * 10000) / _totalSupply;
        feeA = (accumulatedFeesA * userShare) / 10000;
        feeB = (accumulatedFeesB * userShare) / 10000;

        require(feeA > 0 || feeB > 0, "No fees to claim");

        // Transfer fees to user
        if (feeA > 0) {
            tokenA.transfer(msg.sender, feeA);
        }
        if (feeB > 0) {
            tokenB.transfer(msg.sender, feeB);
        }

        emit FeesClaimed(msg.sender, feeA, feeB);
    }

    /**
     * @notice Calculate swap output amount (view function)
     * @param tokenIn Address of token to swap in
     * @param amountIn Amount of token to swap in
     * @return amountOut Estimated amount of token to receive
     */
    function getSwapAmount(address tokenIn, uint256 amountIn)
        external
        view
        returns (uint256 amountOut)
    {
        require(amountIn > 0, "Amount must be greater than 0");
        require(
            tokenIn == address(tokenA) || tokenIn == address(tokenB),
            "Invalid token"
        );

        bool isTokenA = tokenIn == address(tokenA);
        (uint256 reserveIn, uint256 reserveOut) = isTokenA
            ? (reserveA, reserveB)
            : (reserveB, reserveA);

        uint256 amountInWithFee = amountIn * (FEE_DENOMINATOR - FEE_NUMERATOR);
        uint256 numerator = amountInWithFee * reserveOut;
        uint256 denominator = (reserveIn * FEE_DENOMINATOR) + amountInWithFee;
        amountOut = numerator / denominator;
    }

    /**
     * @notice Get current pool state
     */
    function getReserves()
        external
        view
        returns (uint256 _reserveA, uint256 _reserveB, uint256 _totalLiquidity)
    {
        return (reserveA, reserveB, totalSupply());
    }

    /**
     * @notice Get user's liquidity position
     */
    function getUserLiquidity(address user)
        external
        view
        returns (uint256 liquidityAmount, uint256 sharePercentage)
    {
        liquidityAmount = balanceOf(user);
        uint256 _totalSupply = totalSupply();
        sharePercentage = _totalSupply > 0
            ? (liquidityAmount * 10000) / _totalSupply
            : 0;
    }

    /**
     * @notice Calculate price impact of a swap
     * @param amountIn Input amount
     * @param tokenIn Address of token to swap in
     * @return priceImpact Price impact in basis points (e.g., 500 = 5%)
     */
    function calculatePriceImpact(
        uint256 amountIn,
        address tokenIn
    ) external view returns (uint256 priceImpact) {
        bool isTokenA = tokenIn == address(tokenA);
        (uint256 reserveIn, uint256 reserveOut) = isTokenA
            ? (reserveA, reserveB)
            : (reserveB, reserveA);

        return _calculatePriceImpact(amountIn, reserveIn, reserveOut);
    }

    /**
     * @notice Internal function to calculate price impact
     */
    function _calculatePriceImpact(
        uint256 amountIn,
        uint256 reserveIn,
        uint256 reserveOut
    ) internal pure returns (uint256) {
        // Price impact = ((outputAmount * reserveIn) / (inputAmount * reserveOut) - 1) * 100
        uint256 amountInWithFee = amountIn * (FEE_DENOMINATOR - FEE_NUMERATOR);
        uint256 numerator = amountInWithFee * reserveOut;
        uint256 denominator = (reserveIn * FEE_DENOMINATOR) + amountInWithFee;
        uint256 outputAmount = numerator / denominator;

        // Calculate price impact in basis points
        uint256 idealOutput = (amountIn * reserveOut) / reserveIn;
        if (idealOutput == 0) return 0;

        uint256 impact = ((idealOutput - outputAmount) * 10000) / idealOutput;
        return impact;
    }

    /**
     * @notice Update TWAP price observations
     */
    function _updatePrice() internal {
        uint256 timeElapsed = block.timestamp - lastPriceUpdate;

        if (timeElapsed > 0 && reserveA > 0 && reserveB > 0) {
            // Calculate cumulative prices
            uint256 priceACumulative = (reserveB * 1e18) / reserveA;
            uint256 priceBCumulative = (reserveA * 1e18) / reserveB;

            // Add observation
            observations.push(Observation({
                timestamp: block.timestamp,
                priceACumulative: priceACumulative,
                priceBCumulative: priceBCumulative
            }));

            // Remove old observations (keep only last 24 hours)
            _pruneOldObservations();

            lastPriceUpdate = block.timestamp;
        }
    }

    /**
     * @notice Remove observations older than OBSERVATION_WINDOW
     */
    function _pruneOldObservations() internal {
        uint256 cutoffTime = block.timestamp - OBSERVATION_WINDOW;
        uint256 i = 0;

        while (i < observations.length && observations[i].timestamp < cutoffTime) {
            i++;
        }

        if (i > 0) {
            for (uint256 j = 0; j < observations.length - i; j++) {
                observations[j] = observations[j + i];
            }
            for (uint256 k = 0; k < i; k++) {
                observations.pop();
            }
        }
    }

    /**
     * @notice Get TWAP for a specific period
     * @param period Time period in seconds
     * @return twapPriceA Time-weighted average price of A in terms of B
     * @return twapPriceB Time-weighted average price of B in terms of A
     */
    function getTWAP(uint256 period)
        external
        view
        returns (uint256 twapPriceA, uint256 twapPriceB)
    {
        require(observations.length >= 2, "Insufficient observations");
        require(period <= OBSERVATION_WINDOW, "Period too long");

        uint256 targetTime = block.timestamp - period;

        // Find oldest observation within period
        uint256 oldestIndex = observations.length - 1;
        for (uint256 i = 0; i < observations.length; i++) {
            if (observations[i].timestamp >= targetTime) {
                oldestIndex = i;
                break;
            }
        }

        Observation memory oldest = observations[oldestIndex];
        Observation memory newest = observations[observations.length - 1];

        uint256 timeElapsed = newest.timestamp - oldest.timestamp;
        require(timeElapsed > 0, "No time elapsed");

        twapPriceA = (newest.priceACumulative - oldest.priceACumulative) / timeElapsed;
        twapPriceB = (newest.priceBCumulative - oldest.priceBCumulative) / timeElapsed;
    }

    /**
     * @notice Square root function (Babylonian method)
     */
    function _sqrt(uint256 x) internal pure returns (uint256) {
        if (x == 0) return 0;
        uint256 z = (x + 1) / 2;
        uint256 y = x;
        while (z < y) {
            y = z;
            z = (x / z + z) / 2;
        }
        return y;
    }
